methods.code:
if(this.name !== this.display){
    if(!this.display || !this.name || this.display.toLowerCase().replace(/ /g, "") === this.name.toLowerCase().replace(/ /g, "") || this.display.toLowerCase().replace(/ /g, "") === this.name.toLowerCase().replace(/ /g, "").slice(0, -1)){
        const words = this.name.replace(/([A-Z])/g, " $1");
        const upperWords = words.charAt(0).toUpperCase() + words.slice(1);
        this.display = upperWords;
    }
}
